using SaipaShop.Domain.Exceptions;using SaipaShop.Domain.ResultPattern;namespace SaipaShop.Application.Patterns;public class SerialRunner<T>{    private readonly Queue<Func<Result<T>>> _items = new();    public SerialRunner<T> Enqueue(Func<Result<T>> item)    {        _items.Enqueue(item);        return this;    }    public Result<T> Start()    {        if (_items.Count == 0)            throw new NullDomainException("items are empty", "There is no items in the list.");        Result<T> response = null;        foreach (var funcItem in _items)        {            response = funcItem();            if (!response.IsSuccess)                return response;        }        return response;    }}